import { 
    GET_CONTACTS, 
    SET_LOADING, ADD_CONTACT, 
    SET_LOADINGFORM, 
    OPEN_ALERT_ERROR,
    CLOSE_ALERT_ERROR,
    DELETE_CONTACT,
    SET_CONTACT,
    CLEAR_CURRENT,
    FILTER_CONTACTS,
    CLEAR_FILTER} from "../types"

const initialState = {
    contacts: [],
    current: null,
    filtered: null,
    loading: false,
    loadingForm: false,
    alert_error: false,
    error_msg: '',
}

export default (state = initialState, action)=>{
    switch(action.type){
        case GET_CONTACTS:
            return {
                ...state,
                contacts: action.payload,
                loading: false
            }
        case ADD_CONTACT:
            return {
                ...state,
                contacts: [action.payload, ...state.contacts],
                loadingForm: false
            }
        case DELETE_CONTACT:
            return{
                ...state,
                contacts: state.contacts.filter(current => current.id !== action.payload)
            }
        case SET_LOADING:
            return {
                ...state,
                loading: true
            }
        case OPEN_ALERT_ERROR:
            return {
                ...state,
                loadingForm: false,
                error_msg: action.payload,
                alert_error: true
            }
        case CLOSE_ALERT_ERROR:
            return{
                ...state,
                alert_error: false,
                error_msg: '',
            }
        case SET_CONTACT:
            return {
                ...state,
                current: action.payload
            }
        case CLEAR_CURRENT:
            return {
                ...state,
                current: null
            }
        case SET_LOADINGFORM:
            return {
                ...state,
                loadingForm: true
            }
        case FILTER_CONTACTS:
            return {
                ...state,
                filtered: state.contacts.filter(contact =>{
                    const regex = new RegExp(`${action.payload}`, 'gi')
                    return contact.name.match(regex) || contact.email.match(regex)
                })
            }
        case CLEAR_FILTER:
            return {
                ...state,
                filtered: null
            }
        default:
            return state
    }
}
