import React, {useEffect} from 'react'
import {connect} from 'react-redux'
import { 
    getContacts, 
    deleteContact,
    setContact,
    filterContacts,
    clearFilter,
} from '../../redux/actions/contactActions'
import ContactItem from './ContactItem'
import PreLoader from '../layouts/PreLoader'
import ContactFilter from './ContactFilter'

const Contacts = ({state: {contacts, loading, filtered}, getContacts, deleteContact, filterContacts, clearFilter, setContact}) => {
    useEffect(()=>{
        getContacts();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    if(loading || contacts === []){
        return <PreLoader/>
    }

    if(contacts.length === 0){
        return <h4 className="loader">Please add a contact...</h4>
    }
    return (
        <div style={{display: 'flex', flexDirection: 'column'}}>
            <ContactFilter
                filterContacts={filterContacts}
                clearFilter={clearFilter}
                filtered={filtered}
            />
            {filtered !== null ? filtered.map(data => (
                <ContactItem key={data.id}
                    _id={data.id}
                    name={data.name}
                    email={data.email}
                    phone={data.phone}
                    type={data.type}
                    deleteCustomer={deleteCustomer}
                    setContact={setContact}
                />
            )) : contacts.map(data => (
                <ContactItem key={data.id}
                    _id={data.id}
                    first_name={data.first_name}
                    last_name={data.last_name}
                    email={data.email}
                    phone={data.phone}
                    address={data.address}
                    address2={data.address2}
                    deleteContact={deleteCustomer}
                    setCustomer={setCustomer}
                />
            ))}
        </div>
    )
}

const mapStateToProps = state => ({
    state: state.contact,
});

export default connect(mapStateToProps, {getContacts, deleteContact, setContact, filterContacts, clearFilter})(Contacts)
