import React, {useEffect} from 'react'
import {connect} from 'react-redux'
import { 
    getContacts, 
    deleteContact,
    setContact,
    filterContacts,
    clearFilter,
} from '../../redux/actions/contactActions'
import ContactItem from './ContactItem'
import PreLoader from '../layouts/PreLoader'
import ContactFilter from './ContactFilter'

const Contacts = ({state: {contacts, loading, filtered}, getContacts, deleteContact, filterContacts, clearFilter, setCustomer}) => {
    useEffect(()=>{
        getContacts();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    if(loading || customers === []){
        return <PreLoader/>
    }

    if(contacts.length === 0){
        return <h4 className="loader">Please add a contact...</h4>
    }
    return (
        <div style={{display: 'flex', flexDirection: 'column'}}>
            <CustomerFilter
                filterContacts={filterContacts}
                clearFilter={clearFilter}
                filtered={filtered}
            />
            {filtered !== null ? filtered.map(data => (
                <CustomerItem key={data.id}
                    _id={data.id}
                    first_name={data.first_name}
                    last_name={data.last_name}
                    address={data.address}
                    address2={data.address2}
                    city={data.city}
                    state={data.state}
                    state={data.zip}
                    email={data.email}
                    phone={data.phone}
                    deleteCustomer={deleteCustomer}
                    setContact={setContact}
                />
            )) : contacts.map(data => (
                <CustomerItem key={data.id}
                    _id={data.id}
                    first_name={data.first_name}
                    last_name={data.last_name}
                    email={data.email}
                    phone={data.phone}
                    address={data.address}
                    address2={data.address2}
                    city={data.city}
                    state={data.state}
                    state={data.zip}
                    deleteContact={deleteCustomer}
                    setCustomer={setCustomer}
                />
            ))}
        </div>
    )
}

const mapStateToProps = state => ({
    state: state.contact,
});

export default connect(mapStateToProps, {getCustomers, deleteContact, setCustomer, filterCustomers, clearFilter})(Customers)
