import {
    REGISTER_SUCCESS, 
    REGISTER_FAIL,
    LOADING_AUTH,
    LOGIN_SUCCESS,
    LOGIN_FAIL
} from '../types/auth.js'
const initialState = {
    token: localStorage.getItem('token'),
    isAuthenticated: false,
    user: {},
    loading: false,
    success: null,
    error_auth: null,
    error_msg: null
}

export default (state = initialState, action)=>{
    switch (action.type){
        case REGISTER_SUCCESS:
            return {...state, success: action.payload, loading: false}
        case REGISTER_FAIL:
            return {...state, error_msg: action.payload, loading:false}
        case LOADING_AUTH:
            return {...state, loading: true}
        case LOGIN_SUCCESS:
            localStorage.setItem('token', action.payload.token);
            return {
                ...state,
                user: action.payload.user,
                isAuthenticated: true,
                loading: false,
            }
        case LOGIN_FAIL:
            return {
                ...state,
                loading: false,
                isAuthenticated: false,
                error_auth: action.payload
            }
        default:
            return state
    }
}
